                    stroke="currentColor"
                  >
                    <path
                      stroke-linecap="round"
                      stroke-linejoin="round"
                      stroke-width="2"
                      d="M19 9l-7 7-7-7"
                    />
                  </svg>
                </span>
              </button>

              <!-- Submenu -->
              <div *ngIf="item.active" class="space-y-1 pl-4">
                <a
                  *ngFor="let child of item.children"
                  [href]="child.href"
                  [class]="getSubmenuClasses(child.active)"
                >
                  <span class="flex w-full items-center justify-between">
                    <span class="flex items-center">
                      <span
                        *ngIf="child.leftIcon"
                        class="mr-2"
                        [innerHTML]="sanitizeIcon(child.leftIcon)"
                      ></span>
                      <span
                        *ngIf="child.icon"
                        class="mr-3"
                        [innerHTML]="sanitizeIcon(child.icon)"
                      ></span>
                      {{ child.label }}
                    </span>
                    <span *ngIf="child.badge" [class]="getBadgeClasses()">
                      {{ child.badge }}
                    </span>
                  </span>
                </a>
              </div>
            </div>

            <!-- Single item -->
            <ng-template #singleItem>
              <a [href]="item.href" [class]="getMenuItemClasses(item.active)">
                <span class="flex w-full items-center justify-between">
