import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { DomSanitizer, SafeHtml } from '@angular/platform-browser';

interface MenuItem {
  label: string;
  icon?: string;
  leftIcon?: string;
  href: string;
  active?: boolean;
  children?: MenuItem[];
  badge?: string;
}

@Component({
  selector: 'app-sidemenu',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="flex h-screen">
      <!-- Sidebar -->
      <div [class]="containerClasses">
        <!-- Logo section -->
        <div class="flex items-center h-16 px-4 border-b">
          <span class="text-lg font-semibold">{{ brand }}</span>
        </div>

        <!-- Menu items -->
        <nav class="flex-1 px-2 py-4 space-y-1">
          <ng-container *ngFor="let item of menuItems">
            <!-- Item with children -->
            <div *ngIf="item.children; else singleItem" class="space-y-1">
              <button
                (click)="toggleSubmenu(item)"
                [class]="getParentMenuClasses(item.active)"
              >
                <span class="flex items-center">
                  <span *ngIf="item.leftIcon" class="mr-2" [innerHTML]="sanitizeIcon(item.leftIcon)"></span>
                  <span *ngIf="item.icon" class="mr-3" [innerHTML]="sanitizeIcon(item.icon)"></span>
                  {{ item.label }}
                  <span *ngIf="item.badge" [class]="getBadgeClasses()">
                    {{ item.badge }}
                  </span>
                </span>
                <span class="ml-auto">
                  <svg
                    [class]="item.active ? 'transform rotate-180' : ''"
                    class="w-4 h-4 transition-transform"
                    fill="none"
                    viewBox="0 0 24 24"
                    stroke="currentColor"
                  >
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M19 9l-7 7-7-7" />
                  </svg>
                </span>
              </button>

              <!-- Submenu -->
              <div
                *ngIf="item.active"
                class="pl-4 space-y-1"
              >
                <a
                  *ngFor="let child of item.children"
                  [href]="child.href"
                  [class]="getSubmenuClasses(child.active)"
                >
                  <span class="flex items-center justify-between w-full">
                    <span class="flex items-center">
                      <span *ngIf="child.leftIcon" class="mr-2" [innerHTML]="sanitizeIcon(child.leftIcon)"></span>
                      <span *ngIf="child.icon" class="mr-3" [innerHTML]="sanitizeIcon(child.icon)"></span>
                      {{ child.label }}
                    </span>
                    <span *ngIf="child.badge" [class]="getBadgeClasses()">
                      {{ child.badge }}
                    </span>
                  </span>
                </a>
              </div>
            </div>

            <!-- Single item -->
            <ng-template #singleItem>
              <a
                [href]="item.href"
                [class]="getMenuItemClasses(item.active)"
              >
                <span class="flex items-center justify-between w-full">
                  <span class="flex items-center">
                    <span *ngIf="item.leftIcon" class="mr-2" [innerHTML]="sanitizeIcon(item.leftIcon)"></span>
                    <span *ngIf="item.icon" class="mr-3" [innerHTML]="sanitizeIcon(item.icon)"></span>
                    {{ item.label }}
                  </span>
                  <span *ngIf="item.badge" [class]="getBadgeClasses()">
                    {{ item.badge }}
                  </span>
                </span>
              </a>
            </ng-template>
          </ng-container>
        </nav>
      </div>
    </div>
  `
})
export class SidemenuComponent {
  @Input() variant: 'light' | 'dark' = 'light';
  @Input() brand: string = 'Dashboard';
  @Input() menuItems: MenuItem[] = [];
  @Input() collapsed: boolean = false;

  constructor(private sanitizer: DomSanitizer) {}

  sanitizeIcon(icon: string): SafeHtml {
    return this.sanitizer.bypassSecurityTrustHtml(icon);
  }

  get containerClasses(): string {
    const baseClasses = 'flex flex-col w-64 transition-all duration-300';
    return this.variant === 'light'
      ? `${baseClasses} bg-white border-r`
      : `${baseClasses} bg-gray-800 border-gray-700`;
  }

  getMenuItemClasses(isActive: boolean = false): string {
    const baseClasses = 'flex items-center px-4 py-2 text-sm rounded-md transition-colors';

    if (this.variant === 'light') {
      return isActive
        ? `${baseClasses} bg-blue-100 text-blue-700`
        : `${baseClasses} text-gray-700 hover:bg-gray-100`;
    } else {
      return isActive
        ? `${baseClasses} bg-gray-900 text-white`
        : `${baseClasses} text-gray-300 hover:bg-gray-700 hover:text-white`;
    }
  }

  getParentMenuClasses(isActive: boolean = false): string {
    const baseClasses = 'flex items-center justify-between w-full px-4 py-2 text-sm rounded-md transition-colors';

    if (this.variant === 'light') {
      return isActive
        ? `${baseClasses} bg-gray-100 text-gray-900`
        : `${baseClasses} text-gray-700 hover:bg-gray-100`;
    } else {
      return isActive
        ? `${baseClasses} bg-gray-700 text-white`
        : `${baseClasses} text-gray-300 hover:bg-gray-700 hover:text-white`;
    }
  }

  getSubmenuClasses(isActive: boolean = false): string {
    const baseClasses = 'flex items-center px-4 py-2 text-sm rounded-md transition-colors';

    if (this.variant === 'light') {
      return isActive
        ? `${baseClasses} bg-blue-50 text-blue-700`
        : `${baseClasses} text-gray-600 hover:bg-gray-50`;
    } else {
      return isActive
        ? `${baseClasses} bg-gray-700 text-white`
        : `${baseClasses} text-gray-400 hover:bg-gray-700 hover:text-white`;
    }
  }

  getBadgeClasses(): string {
    const baseClasses = 'ml-2 px-2 py-0.5 text-xs font-medium rounded-full';
    return this.variant === 'light'
      ? `${baseClasses} bg-blue-100 text-blue-800`
      : `${baseClasses} bg-gray-700 text-gray-200`;
  }

  toggleSubmenu(item: MenuItem): void {
    item.active = !item.active;
  }
}

import { Component, Input, ElementRef, HostListener } from '@angular/core';
import { CommonModule } from '@angular/common';

type TooltipPosition = 'top' | 'right' | 'bottom' | 'left';
type TooltipVariant = 'dark' | 'light' | 'info' | 'warning' | 'error';

@Component({
  selector: 'app-tooltip',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="relative inline-block">
      <!-- Tooltip trigger -->
      <div (mouseenter)="show()" (mouseleave)="hide()">
        <ng-content></ng-content>
      </div>

      <!-- Tooltip content -->
      <div *ngIf="isVisible" 
           [class]="tooltipClasses"
           [style.margin-left.px]="position === 'top' || position === 'bottom' ? offsetX : 0"
           [style.margin-top.px]="position === 'left' || position === 'right' ? offsetY : 0">
        {{ content }}
        <div [class]="arrowClasses"></div>
      </div>
    </div>
  `
})
export class TooltipComponent {
  @Input() content: string = '';
  @Input() position: TooltipPosition = 'top';
  @Input() variant: TooltipVariant = 'dark';
  @Input() delay: number = 0;

  isVisible: boolean = false;
  offsetX: number = 0;
  offsetY: number = 0;

  private showTimeout?: number;

  constructor(private elementRef: ElementRef) {}

  get tooltipClasses(): string {
    const baseClasses = 'absolute z-50 px-2 py-1 text-sm rounded shadow-lg whitespace-nowrap';
    const positionClasses = {
      top: 'bottom-full left-1/2 transform -translate-x-1/2 mb-2',
      right: 'left-full top-1/2 transform -translate-y-1/2 ml-2',
      bottom: 'top-full left-1/2 transform -translate-x-1/2 mt-2',
      left: 'right-full top-1/2 transform -translate-y-1/2 mr-2'
    };
    const variantClasses = {
      dark: 'bg-gray-900 text-white',
      light: 'bg-white text-gray-900 border border-gray-200',
      info: 'bg-blue-600 text-white',
      warning: 'bg-yellow-500 text-white',
      error: 'bg-red-600 text-white'
    };

    return `${baseClasses} ${positionClasses[this.position]} ${variantClasses[this.variant]}`;
  }

  get arrowClasses(): string {
    const baseClasses = 'absolute w-2 h-2 transform rotate-45';
    const positionClasses = {
      top: 'bottom-[-4px] left-1/2 -translate-x-1/2',
      right: 'left-[-4px] top-1/2 -translate-y-1/2',
      bottom: 'top-[-4px] left-1/2 -translate-x-1/2',
      left: 'right-[-4px] top-1/2 -translate-y-1/2'
    };
    const variantClasses = {
      dark: 'bg-gray-900',
      light: 'bg-white border-b border-r border-gray-200',
      info: 'bg-blue-600',
      warning: 'bg-yellow-500',
      error: 'bg-red-600'
    };

    return `${baseClasses} ${positionClasses[this.position]} ${variantClasses[this.variant]}`;
  }

  show(): void {
    if (this.delay) {
      this.showTimeout = window.setTimeout(() => {
        this.isVisible = true;
        this.updatePosition();
      }, this.delay);
    } else {
      this.isVisible = true;
      this.updatePosition();
    }
  }

  hide(): void {
    if (this.showTimeout) {
      clearTimeout(this.showTimeout);
    }
    this.isVisible = false;
  }

  private updatePosition(): void {
    const element = this.elementRef.nativeElement;
    const tooltip = element.querySelector('[class*="absolute"]');
    if (!tooltip) return;

    const elementRect = element.getBoundingClientRect();
    const tooltipRect = tooltip.getBoundingClientRect();

    // Adjust position if tooltip is outside viewport
    if (this.position === 'top' || this.position === 'bottom') {
      const leftOverflow = tooltipRect.left < 0;
      const rightOverflow = tooltipRect.right > window.innerWidth;

      if (leftOverflow) {
        this.offsetX = -tooltipRect.left + 8;
      } else if (rightOverflow) {
        this.offsetX = window.innerWidth - tooltipRect.right - 8;
      }
    } else {
      const topOverflow = tooltipRect.top < 0;
      const bottomOverflow = tooltipRect.bottom > window.innerHeight;

      if (topOverflow) {
        this.offsetY = -tooltipRect.top + 8;
      } else if (bottomOverflow) {
        this.offsetY = window.innerHeight - tooltipRect.bottom - 8;
      }
    }
  }

  @HostListener('window:scroll')
  @HostListener('window:resize')
  onWindowChange(): void {
    if (this.isVisible) {
      this.updatePosition();
    }
  }
}

import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-typography',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div [class]="classes">
      {{ text }}
    </div>
  `
})
export class TypographyComponent {
  @Input() variant: 'h1' | 'h2' | 'h3' | 'h4' | 'subtitle1' | 'subtitle2' | 'body1' | 'body2' | 'caption' = 'body1';
  @Input() text: string = '';

  get classes(): string {
    const variantClasses = {
      h1: 'text-5xl font-bold text-gray-900 leading-tight',
      h2: 'text-4xl font-semibold text-gray-900 leading-tight',
      h3: 'text-3xl font-semibold text-gray-900 leading-snug',
      h4: 'text-2xl font-medium text-gray-900 leading-snug',
      subtitle1: 'text-xl font-medium text-gray-800 leading-relaxed',
      subtitle2: 'text-lg font-medium text-gray-800 leading-relaxed',
      body1: 'text-base text-gray-700 leading-relaxed',
      body2: 'text-sm text-gray-700 leading-relaxed',
      caption: 'text-xs text-gray-600 leading-normal'
    };

    return variantClasses[this.variant];
  }
}

